mapper.select:
SELECT
    hour,
    SUM(pv) AS pv
FROM
    t_link_access_stats
WHERE
    full_short_url = #{fullShortUrl}
  AND gid = #{gid}
  AND date BETWEEN #{startDate}
  and #{endDate}
GROUP BY
    full_short_url, gid, hour

SELECT weekday,
       SUM(pv) AS pv
FROM t_link_access_stats
WHERE full_short_url = #{fullShortUrl}
  AND gid = #{gid}
  AND date BETWEEN #{startDate}
  and #{endDate}
GROUP BY
    full_short_url, gid, weekday
