mapper.select:
select `gid`, count(*) as 'shortLinkCount' from t_link where `enable_status` = 0 and `gid` in
#{gid}
group by `gid`

mapper.update:
update t_link
set total_pv  = total_pv + #{totalPv},
    total_uv  = total_uv + #{totalUv},
    total_uip = total_uip + #{totalUip}
where gid = #{gid}
  and full_short_url = #{fullShortUrl}
